create table cfg_queries
(
    id              int auto_increment
        primary key,
    name            varchar(50)   not null,
    label           varchar(70)   not null,
    tsmquery_v5     varchar(500)  not null,
    tsmquery_v6     varchar(500)  not null,
    fields          varchar(1000) not null,
    timetablefields varchar(255)  null,
    info            varchar(500)  null,
    orderby         varchar(35)   null,
    alert_comp      varchar(35)   null,
    alert_val       varchar(35)   null,
    alert_field     varchar(35)   null,
    alert_col       varchar(35)   null,
    notforlibclient tinyint(1)    null,
    polltype        varchar(35)   not null,
    pollfreq        int           not null,
    parent          varchar(35)   not null,
    sortorder       smallint      not null,
    constraint name
        unique (name)
)
    collate = utf8_unicode_ci;

